mapper.update:
update attendance set CHECK_OUT = CURRENT_TIMESTAMP
, ATTENDANCE_STATUS = #{attendanceStatus}
where (MEMBER_ID = #{memberId}) and (to_char(ATTENDANCE_DATE, 'YYYY-MM-DD') = #{today})

UPDATE STATISTICS
SET


	ABSENT = (
			SELECT
				(S.ABSENT+1) AS ABSENT
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(A.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=to_number(#{month})
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	attendance = (
			SELECT 
				attendance+1 AS attendance
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	late = (
			SELECT 
				late+1 AS late
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}


	leave = (
			SELECT 
				leave+1 AS leave
			FROM
				STATISTICS S, ATTENDANCE A
			WHERE
				S.MEMBER_ID=A.MEMBER_ID 
			AND
				to_char(a.attendance_date,'YYYY-MM-DD')=#{yesterday}
			AND
				to_char(YEAR)=#{year}
			AND
				to_char(month)=#{month}
			AND
				A.MEMBER_ID = #{memberId}
			)
	WHERE member_id=#{memberId}
