mapper.select:

				select 
				    to_char(CHECK_IN, 'YYYY/MM/dd HH24:mi:ss') as checkIn , 
				    to_char(CHECK_OUT, 'YYYY/MM/dd HH24:mi:ss') as checkOut 	

			    from(
			        select * 
			        from out
			        where MEMBER_ID = #{memberId} and to_char(OUT_DATE, 'YYYY-MM-DD') = #{today}
			        order by check_in desc

			    )
   				where rownum = 1

mapper.update:

update out
set CHECK_OUT = #{timestamp}
where to_char(OUT_DATE, 'YYYY-MM-DD') = to_char(#{today}, 'YYYY-MM-DD') and CHECK_OUT is null and CHECK_IN is not null

mapper.select:

select distinct member_id
from out
where to_char(out_date,'YYYY-MM-DD') = #{today}
